import { NavLink } from "react-router-dom";
import s from "./Dialogs.module.css";

const DialogItem = (props) => {
  let path = "/dialogs/" + props.id;

  return (
    <div className={s.dialog + " " + s.active}>
      <NavLink to={path}>{props.name}</NavLink>
    </div>
  );
};

const Message = (props) => {
  return <div className={s.message}>{props.text}</div>;
};

const Dialogs = (props) => {
  let dialogsData = [
    { id: 1, name: "Vadick" },
    { id: 2, name: "Alina" },
    { id: 3, name: "Chesterok" },
    { id: 4, name: "Puppy" },
    { id: 5, name: "Another Puppy" },
    { id: 6, name: "Another cat" },
  ];

  let dialogsElements = [
    dialogsData.map((el) => {
      return <DialogItem name={dialogsData[el].name} id={dialogsData[el].id} />;
    }),
  ];

  let messagesData = [
    { id: 1, message: "Hi" },
    { id: 2, message: "Mew Mew" },
    { id: 3, message: "Bark Bark" },
  ];

  return (
    <div className={s.dialogs}>
      <div className={s.dialogsItems}>
        {dialogsElements}
        {/* <DialogItem name={dialogsData[0].name} id={dialogsData[0].id} />
        <DialogItem name={dialogsData[1].name} id={dialogsData[1].id} />
        <DialogItem name={dialogsData[2].name} id={dialogsData[2].id} />
        <DialogItem name={dialogsData[3].name} id={dialogsData[3].id} />
        <DialogItem name={dialogsData[4].name} id={dialogsData[4].id} />
        <DialogItem name={dialogsData[5].name} id={dialogsData[5].id} /> */}
      </div>
      <div className={s.messages}>
        <Message text={messagesData[0].message} />
        <Message text={messagesData[1].message} />
        <Message text={messagesData[2].message} />
      </div>
    </div>
  );
};

export default Dialogs;
