import axios from "axios";
import s from "./Users.module.css";

const Users = (props) => {
  if (props.users.length === 0) {
    axios
      .get("https://social-network.samuraijs.com/api/1.0/users")
      .then((response) => {});
    props.setUsers([
      {
        id: 1,
        photoUrl:
          "https://static.mk.ru/upload/entities/2018/09/25/authors/picture/b2/e4/eb/b6/2388277_7926811.jpg",
        followed: false,
        fullName: "Vadim",
        status: "I am a boss",
        location: { city: "Rostov", country: "russia" },
      },
      {
        id: 2,
        photoUrl:
          "https://static.mk.ru/upload/entities/2018/09/25/authors/picture/b2/e4/eb/b6/2388277_7926811.jpg",
        followed: true,
        fullName: "Sasha",
        status: "I am a boss too",
        location: { city: "Kiev", country: "Ukrain" },
      },
      {
        id: 3,
        photoUrl:
          "https://static.mk.ru/upload/entities/2018/09/25/authors/picture/b2/e4/eb/b6/2388277_7926811.jpg",
        followed: false,
        fullName: "Dima",
        status: "I am a boss too",
        location: { city: "Minsk", country: "Belarus" },
      },
    ]);
  }

  return (
    <div>
      {props.users.map((u) => (
        <div key={u.id}>
          <span>
            <div>
              <img src={u.photoUrl} alt="img" className={s.userPhoto} />
            </div>
            <div>
              {u.followed ? (
                <button
                  onClick={() => {
                    props.unfollow(u.id);
                  }}
                >
                  unfollow
                </button>
              ) : (
                <button
                  onClick={() => {
                    props.follow(u.id);
                  }}
                >
                  follow
                </button>
              )}
            </div>
          </span>
          <span>
            <span>
              <div>{u.fullName}</div>
              <div>{u.status}</div>
            </span>
            <span>
              <div>{u.location.country}</div>
              <div>{u.location.city}</div>
            </span>
          </span>
        </div>
      ))}
    </div>
  );
};
export default Users;
