import { NavLink } from "react-router-dom";
import s from "./Dialogs.module.css";

const DialogItem = (props) => {
  let path = "/dialogs/" + props.id;

  return (
    <div className={s.dialog + " " + s.active}>
      <NavLink to={path}>{props.name}</NavLink>
    </div>
  );
};

const Message = (props) => {
  return <div className={s.message}>{props.text}</div>;
};

const Dialogs = (props) => {
  let Dia;
  return (
    <div className={s.dialogs}>
      <div className={s.dialogsItems}>
        <DialogItem name="Vadick" id="1" />
        <DialogItem name="Alina" id="2" />
        <DialogItem name="Chesterok" id="3" />
        <DialogItem name="Puppy" id="4" />
        <DialogItem name="Another Puppy" id="5" />
        <DialogItem name="Another cat" id="6" />
      </div>
      <div className={s.messages}>
        <Message text="Hi" />
        <Message text="Mew mew" />
        <Message text="Bark bark" />
      </div>
    </div>
  );
};

export default Dialogs;
